import React, { useEffect, useRef } from 'react';
import * as d3 from 'd3';

const WordCloud = ({ filepath, category }) => {
    const ref = useRef();

    useEffect(() => {
        fetch(`http://localhost:3000/word-cloud/${btoa(filepath)}/${category}`)
        .then(response => response.json())
        .then(data => {
            if (data.error) {
                console.error(data.error);
                return;
            }

            // Here you will use D3.js to generate the word cloud
            const svg = d3.select(ref.current)
                .append("svg")
                .attr("width", 500)
                .attr("height", 500);

            // The rest of your D3.js code goes here
        });
    }, [filepath, category]);

    return (
        <div ref={ref}>
            {/* This is where your word cloud will be rendered */}
        </div>
    );
};

export default WordCloud;
