import React, { useEffect, useRef } from 'react';
import * as d3 from 'd3';

const BubbleChart = ({ filepath }) => {
    const ref = useRef();

    useEffect(() => {
        if (!filepath) {
            return;
        }

        // Fetch data from the server
        fetch(`http://localhost:3000/word-cloud/${btoa(filepath)}/all`)
        .then(response => response.json())
        .then(rawData => {
            const data = Object.entries(rawData).map(([id, value]) => ({ id, value }));
            console.log(data)

            const width = 928;
            const height = width;
            const margin = 1;

            const format = d3.format(",d");
            const color = d3.scaleOrdinal(d3.schemeTableau10);

            const pack = d3.pack()
                .size([width - margin * 2, height - margin * 2])
                .padding(3);

            const hierarchyData = d3.hierarchy({ children: data })
                .sum(d => d.value);

            const root = pack(hierarchyData);

            const svg = d3.select(ref.current)
                .append("svg")
                .attr("width", width)
                .attr("height", height)
                .attr("viewBox", [-margin, -margin, width, height])
                .attr("style", "max-width: 100%; height: auto; font: 10px sans-serif;")
                .attr("text-anchor", "middle")
                .append("svg")

            const node = svg.append("g")
              .selectAll("g")
              .data(root.leaves())
              .join("g")
                .attr("transform", d => `translate(${d.x},${d.y})`);

            node.append("circle")
                .attr("fill-opacity", 0.7)
                .attr("fill", d => color(d.data.id))
                .attr("r", d => d.r);

            node.append("text")
                .text(d => d.data.id)
                .attr("fill", "#000")
                .attr("font-size", "10px");

        });

        return () => {
            d3.select(ref.current).selectAll("svg").remove();
        }

    }, [filepath]);


};

export default BubbleChart;
