import React, { useEffect } from 'react';
import * as d3 from 'd3';

function WordCloud({ fileName }) {
  useEffect(() => {
    // Fetch the word data from the server and generate the word cloud
    d3.json(`http://localhost:3001/get-data/${fileName}`).then(function(words) {
      // The word cloud generation code goes here
    });
  }, [fileName]);


  return (
    <div id="word-cloud"></div>
  );
}

export default WordCloud;
