import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { LineChart, Line, CartesianGrid, XAxis, YAxis, Tooltip, Legend, Brush, Label } from 'recharts';
import Modal from 'react-modal';
import '../styles/sentiment.css';

function SentimentVis() {
    const { filename } = useParams(); // Get filename from URL parameters
    const [sentimentData, setSentimentData] = useState(null);
    const [selectedSentiments, setSelectedSentiments] = useState(['compound']); // Initialize with compound sentiment only
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [hoveredIndex, setHoveredIndex] = useState(null);

    const sentimentColors = {
        'compound': '#FF8133',
        'pos': '#2E933C',
        'neu': '#51B9F6',
        'neg': '#FF5F5C'
    };

    const getSentimentClass = (score) => {
        if (score < -0.5) {
            return 'neg';
        } else if (score > 0.5) {
            return 'pos';
        } else {
            return 'neu';
        }
    }

    useEffect(() => {
        const fetchData = async () => {
            const response = await fetch(`http://localhost:3000/sentiment-data/${filename}`);
            const data = await response.json();

            const transformedData = data.neg.map((value, index) => {
                return {
                    index: index,
                    neg: parseFloat(data.neg[index].toFixed(3)),
                    neu: parseFloat(data.neu[index].toFixed(3)),
                    pos: parseFloat(data.pos[index].toFixed(3)),
                    compound: parseFloat(data.compound[index].toFixed(3)),
                    text: data.text[index],
                    wordSentiments: data.wordSentiments[index]
                }
            });

            setSentimentData(transformedData);
        }
        fetchData();
    }, [filename]); 

    const CustomTooltip = ({ active, payload, label }) => {
        if (active && payload && payload.length) {
            setHoveredIndex(label);

            return (
                <div className="custom-tooltip" style={{ backgroundColor: '#f4f4f4', padding: '5px', border: '1px solid #d4d4d4' }}>
                    {payload.map((pl, index) => 
                        <p key={index}>{`${pl.dataKey} score: ${pl.value}`}</p>
                    )}
                    {/* Display the snippet */}
                    {/* <p>Corresponding text: {snippet}</p> */}
                </div>
            );
        }
        return null;
    };

    const handleCheckboxChange = (event) => {
        if (selectedSentiments.includes(event.target.value)) {
            setSelectedSentiments(selectedSentiments.filter(sentiment => sentiment !== event.target.value));
        } else {
            setSelectedSentiments([...selectedSentiments, event.target.value]);
        }
    }


    return (
        <div style={{padding: '20px'}}>
            {sentimentData ? (
                <div>
                    <header style={{ marginBottom: '20px', padding: '10px', borderRadius: '10px', textAlign: 'center', border: '1px solid #ccc', boxShadow: '2px 2px 8px rgba(0,0,0,0.1)' }}>
                        <h1>Sentiment scores: {filename}</h1>
                    </header>
                    <div style={{ display: 'flex', alignItems: 'flex-start' }}>
                    <div style={{ flex: 1, marginRight: '10px' }}>
                            {/* Moved checkbox list to top right corner of the chart */}
                            <div style={{ display: 'flex', justifyContent: 'flex-end', marginBottom: '10px', border: '1px solid #ccc', borderRadius: '8px', boxShadow: '2px 2px 8px rgba(0,0,0,0.1)', padding: '10px', overflow: 'auto', maxWidth: '30%' }}>
                                {Object.keys(sentimentColors).map(sentiment => (
                                    <div key={sentiment} style={{ marginLeft: '10px' }}>
                                        <input 
                                            type="checkbox"
                                            id={sentiment}
                                            value={sentiment}
                                            checked={selectedSentiments.includes(sentiment)}
                                            onChange={handleCheckboxChange}
                                        />
                                        <label htmlFor={sentiment}>{sentiment.charAt(0).toUpperCase() + sentiment.slice(1)}</label>
                                    </div>
                                ))}
                            </div>

                            <Modal
                                    isOpen={isModalOpen}
                                    onRequestClose={() => setIsModalOpen(false)}
                                    contentLabel="Text Modal"
                                >
                                    <button onClick={() => setIsModalOpen(false)}>Close</button>
                            </Modal>
                            <LineChart
                                className='myChart'
                                width={1000}
                                height={500}
                                data={sentimentData}
                                animationDuration={300} 
                                animationEasing="ease-out"
                                margin={{ top: 5, right: 30, left: 20, bottom: 5 }}
                            >
                                <CartesianGrid strokeDasharray="3 3" />
                                <XAxis dataKey="index">
                                    <Label value="Index" offset={-15} position="insideBottom" />
                                </XAxis>
                                <YAxis>
                                    <Label value="Score" angle={-90} position="insideLeft" />
                                </YAxis>
                                <Tooltip content={<CustomTooltip />} />
                                <Legend verticalAlign="top" wrapperStyle={{ lineHeight: '40px' }} />
                                {selectedSentiments.map(sentiment => 
                                    <Line key={sentiment} type="monotone" dataKey={sentiment} stroke={sentimentColors[sentiment]} strokeWidth={3} dot={false}/>
                                )}
                                <Brush heigh={30} stroke="#8884d8" />
                            </LineChart>
                        </div>

                        {/* Styling for the card that will contain the full text */}
                        <div style={{ flex: 1, padding: '10px', border: '1px solid #ccc', borderRadius: '8px', heigh:'auto', overflow: 'scroll', marginTop: '100px', boxShadow: '2px 2px 8px rgba(0,0,0,0.1)' }}>
                            {hoveredIndex !== null && (
                                <div>
                                    <h2>Full Text for Index {hoveredIndex}</h2>
                                    {sentimentData[hoveredIndex].wordSentiments.map(({ word, score }) => (
                                        <span className={`word ${getSentimentClass(score)}`}>{word} </span>
                                        ))}
                                    {/* <p>{sentimentData[hoveredIndex].text}</p> */}
                                </div>
                            )}
                        </div>
                    </div>
                </div>
            ) : (
                <p>Loading sentiment data...</p>
            )}
        </div>
    );



}

export default SentimentVis;
