import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';


function WordFrequency() {
    const [selectedFile, setSelectedFile] = useState();
    const navigate = useNavigate();

    const handleFileChange = (event) => {
        setSelectedFile(event.target.files[0]);
    };

    const handleFileUpload = () => {
        if (!selectedFile) {
            alert('No file selected!');
            return;
        }

        const formData = new FormData();
        formData.append('pdf', selectedFile);

        fetch('http://localhost:3000/upload-wordcloud', {
            method: 'POST',
            body: formData
        })
        .then(response => response.json())
        .then(data => {
            navigate(`/visualisations/word_frequency/${data.path}`);
        })
        .catch(error => {
            console.error('Error:', error);
        });
    };

    return (
        <div>
            <h1>Word Frequency Visualisation</h1>
            <input type="file" accept=".pdf" onChange={handleFileChange} />
            <button onClick={handleFileUpload}>Upload</button>
            {/* Render the WordCloud component for each category */}
            <WordCloud filepath={selectedFile ? selectedFile.name : ''} category="environmental" />
            <WordCloud filepath={selectedFile ? selectedFile.name : ''} category="social" />
            <WordCloud filepath={selectedFile ? selectedFile.name : ''} category="governance" />

        </div>
    );
}

export default WordFrequency;
