import React, { useState, useEffect, useRef } from 'react';
import * as d3 from 'd3';
import { useParams } from 'react-router-dom';
import '../styles/TreeDiagram.css';

function TreeVisualisation() {
  const { filename: filenameFromParams } = useParams();
// const [filenameFromParams, setFilenameFromParams] = useState('Default File Name');
const ref = useRef(null);

  useEffect(() => {
    drawTree(filenameFromParams);
  }, [filenameFromParams]);

  const drawTree = (filename) => {
    let i = 0;
    const duration = 750;
    let root;

    d3.select(ref.current).selectAll("*").remove();

    var margin = {top: 20, right: 90, bottom: 30, left: 90},
        width = 2000 - margin.left - margin.right,
        height = 1000 - margin.top - margin.bottom;

    var svg = d3.select(ref.current)
        .attr("width", "100%")
        .attr("height", "100%")
        .attr("viewBox", `0 0 ${width + margin.right + margin.left} ${height + margin.top + margin.bottom}`)
        .attr("preserveAspectRatio", "xMidYMid meet")
        .append("g")
        .attr("transform", "translate(" + margin.left + "," + margin.top + ")");

    var treemap = d3.tree().size([height, width]);

    d3.json(`http://localhost:3000/processed_data/${filename}`)
    .then((treeData) => { 
      root = d3.hierarchy(treeData, function(d) { return d.children; });
      root.x0 = height / 2;
    //   root.y0 = width / 2;
      root.y0 = 0;
      // Collapse after the second level
      root.children.forEach(collapse);

      update(root);
    })
    .catch(error => {
      console.error("Error occurred while fetching and processing data: ", error);
    });

    function collapse(d) {
      if(d && d.children) {
          d._children = d.children;
          d._children.forEach(collapse);
          d.children = null;
      }
    }

    function update(source) {

            // Assigns the x and y position for the nodes
        var treeData = treemap(root);

        // adjust these values based on how much padding you want around the tree
        var treePadding = 100;

        // find the maximum and minimum x and y values
        var xMax = d3.max(treeData.descendants(), function(d) { return d.x; });
        var xMin = d3.min(treeData.descendants(), function(d) { return d.x; });
        var yMax = d3.max(treeData.descendants(), function(d) { return d.y; });

        // Compute the new tree layout.
        var nodes = treeData.descendants(),
            links = treeData.descendants().slice(1);

        // Normalize for fixed-depth.
        nodes.forEach(function(d){ d.y = d.depth * 1200});



        // ****************** Nodes section ***************************

  // Update the nodes...
  var node = svg.selectAll('g.node')
  .data(nodes, function(d) {return d.id || (d.id = ++i); });

// Enter any new modes at the parent's previous position.
var nodeEnter = node.enter().append('g')
  .attr('class', 'node')
  .attr("transform", function(d) {
    return "translate(" + source.y0 + "," + source.x0 + ")";
})
.on('click', click);

// Add Circle for the nodes
nodeEnter.append('circle')
  .attr('class', 'node')
  .attr('r', 1e-6)
  .style("fill", function(d) {
      return d._children && d._children_length > 0 ? "lightsteelblue" : "#fff";
  });

// Add labels for the nodes
nodeEnter.append('text')
  .attr("dy", ".85em")
  .attr("x", function(d) {
    return (d.children && d.children.length > 0) || (d._children && d._children.length > 0) ? -13 : 13;
  })
  .attr("text-anchor", function(d) {
      return d.children || d._children ? "end" : "start";
  })
  .text(function(d) { return d.data.name; });

// UPDATE
var nodeUpdate = nodeEnter.merge(node);

// Transition to the proper position for the node
nodeUpdate.transition()
.duration(duration)
.attr("transform", function(d) { 
    return "translate(" + d.y + "," + d.x + ")";
 });

// Update the node attributes and style
nodeUpdate.select('circle.node')
.attr('r', 15)
.style("fill", function(d) {
    return (d._children && d._children.length > 0) ? "lightsteelblue" : "#fff";
})
.attr('cursor', 'pointer');


nodeUpdate.select('circle.node')
.attr('r', 15)
.style("fill", function(d) {
// change the fill color depending on whether the node has children or not
if (d._children) {  // collapsed nodes
  return "lightsteelblue"; 
} else if (d.children) {  // expanded nodes
  return "green";  // change this to whatever color you want for expanded nodes
} else {  // leaf nodes
  return "#fff";
}
})
.attr('cursor', 'pointer');


// Remove any exiting nodes
var nodeExit = node.exit().transition()
  .duration(duration)
  .attr("transform", function(d) {
      return "translate(" + source.y + "," + source.x + ")";
  })
  .remove();

// On exit reduce the node circles size to 0
nodeExit.select('circle')
.attr('r', 1e-5);

// On exit reduce the opacity of text labels
nodeExit.select('text')
.style('fill-opacity', 1e-5);

// ****************** links section ***************************

// Update the links...
var link = svg.selectAll('path.link')
  .data(links, function(d) { return d.id; });

// Enter any new links at the parent's previous position.
var linkEnter = link.enter().insert('path', "g")
  .attr("class", "link")
  .attr('d', function(d){
    var o = {x: source.x0, y: source.y0}
    return diagonal(o, o)
  });

// UPDATE
var linkUpdate = linkEnter.merge(link);

// Transition back to the parent element position
linkUpdate.transition()
  .duration(duration)
  .attr('d', function(d){ return diagonal(d, d.parent) });

// Remove any exiting links
var linkExit = link.exit().transition()
  .duration(duration)
  .attr('d', function(d) {
    var o = {x: source.x, y: source.y}
    return diagonal(o, o)
  })
  .remove();

// Store the old positions for transition.
nodes.forEach(function(d){
d.x0 = d.x;
d.y0 = d.y;
});

// Get the bounds of the tree
var bounds = svg.node().getBBox();
var x0 = Infinity;
var x1 = -x0;
nodes.forEach(function(d) {
if (d.x > x1) x1 = d.x;
if (d.x < x0) x0 = d.x;
});

// Calculate the scale to fit the tree into view
var scale = Math.min(width / bounds.width, height / bounds.height);



// Adjust the height of the svg to fit the tree
height = x1 - x0 + margin.top + margin.bottom;

svg.attr("height", height);

// Creates a curved (diagonal) path from parent to the child nodes
function diagonal(s, d) {

let path = `M ${s.y} ${s.x}
C ${(s.y + d.y) / 2} ${s.x},
    ${(s.y + d.y) / 2} ${d.x},
    ${d.y} ${d.x}`;


return path
}

