import React, { useState } from 'react';

function SearchPage() {
  const [companies, setCompanies] = useState([]);
  const [query, setQuery] = useState('');

  function search(query) {
    return fetch(`http://localhost:3000/search?query=${query}`)
      .then(response => response.json());
  }

  function handleSearch(e) {
    e.preventDefault();
    search(query)
      .then(setCompanies);  // Update the `companies` state.
  }

  function handleClear() {
    setQuery('');
    setCompanies([]);
  }

  return (
    <div className='m-4'>
      <form onSubmit={handleSearch}>
        <input 
          name="search" 
          type="text" 
          placeholder="Search for companies..." 
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
        <button type="submit">Search</button>
        <button type="button" onClick={handleClear}>Clear</button>
      </form>

      {/* Render the company data. */}
      {companies.map(company => (
        <div key={company.id} className='card'>
          <div className='card-body'>
          <h5 className="card-title">{company.company_name}</h5>
          <p className="card-text">{company.company_introduction}</p>
          <p><strong>Sector:</strong> {company.sector}</p>
          <p><strong>Industry:</strong> {company.industry}</p>
          <p><strong>Exchange:</strong> {company.exchange}</p>
          <p><strong>Location:</strong> {company.location}</p>
          <a href={company.website} className="btn btn-primary">Visit Website</a>
          </div>
        </div>
      ))}
    </div>
  );
}

export default SearchPage;
