import React, { useEffect, useRef } from 'react';
import * as d3 from 'd3';

const BubbleChart = ({ filepath }) => {
    const ref = useRef();

    useEffect(() => {
        if (!filepath) {
            return;

        }

        const categories = {
            environmental: [
                'clean', 'environmental', 'epa', 'sustainability', 'climate', 'warming', 'biofuels', 'biofuel',
                'green', 'renewable', 'solar', 'stewardship', 'wind', 'emission', 'emissions', 'ghg', 'ghgs'],
            social:  [
                'citizen', 'citizens', 'csr', 'disabilities', 'disability', 'disabled', 'human', 'nations',
                'social', 'un', 'veteran', 'veterans', 'vulnerable', 'children', 'epidemic', 'health', 'healthy'],
            governance: [
                'align', 'aligned', 'aligning', 'alignment', 'aligns', 'bylaw', 'bylaws', 'charter']
        };

        const currentRef = ref.current; // Copy ref.current to a variable

        fetch(`http://localhost:3000/word-cloud/${btoa(filepath)}/all`)
        .then(response => response.json())
        .then(rawData => {
            const data = Object.entries(rawData).map(([id, value]) => ({ id, value }));
            console.log(data);

            const width = 1500; // Changed from 928
            const height = width;
            const margin = 1;

            const format = d3.format(",d");

            const color = d => {
                const categoryColors = {
                    environmental: d3.scaleSequential([5, 10], d3.interpolateGreens),
                    social: d3.scaleSequential([5, 10], d3.interpolateOranges),
                    governance: d3.scaleSequential([5, 10], d3.interpolateBlues)
                };
                for (const [category, keywords] of Object.entries(categories)) {
                    if (keywords.includes(d.data.id)) {
                        return categoryColors[category](Math.random() * 10); // For the example, we use random numbers.
                    }
                }
                return '#ccc'; // Default color
            };


            const pack = d3.pack()
                .size([width - margin * 2, height - margin * 2])
                .padding(3);

            const hierarchyData = d3.hierarchy({ children: data })
                .sum(d => d.value);

            const root = pack(hierarchyData);

            const svg = d3.select(ref.current)
                .append("svg")
                .attr("width", width)
                .attr("height", height)
                .attr("viewBox", [-margin, -margin, width, height])
                .attr("style", "max-width: 100%; height: auto; font: 10px sans-serif;")
                .attr("text-anchor", "middle");

            const node = svg.append("g")
              .selectAll("g")
              .data(root.leaves())
              .join("g")
                .attr("transform", d => `translate(${d.x},${d.y})`);

                node.append("circle")
                .attr("fill-opacity", 0.7)
                .attr("fill", color)
                .attr("r", d => d.r)
                .on("mouseover", function(event, d) {
                    d3.select(this)
                        .transition()
                        .duration(200)
                        .attr("r", d.r * 1.2); // Increase size by 20%



                      // Add a background rectangle for the text label
                    svg.append("rect")
                    .attr("id", "hoverLabelBg")
                    .attr("x", d.x + d.r + 5)
                    .attr("y", d.y - 8)
                    .attr("rx", 4)
                    .attr("ry", 4)
                    .attr("width", 70) // You might want to adjust this
                    .attr("height", 20) // You might want to adjust this
                    .attr("fill", "#ffffff")
                    .attr("stroke", "#000")
                    .attr("stroke-width", "1px");

                    // Add a text label for the word frequency
                    svg.append("text")
                    .attr("id", "hoverLabel")
                    .attr("x", d.x + d.r + 10)
                    .attr("y", d.y + 5)
                    .text(`Frequency: ${d.data.value}`)
                    .attr("font-size", "12px")
                    .attr("fill", "#000");
                })
                .on("mouseout", function() {
                    d3.select(this)
                        .transition()
                        .duration(200)
                        .attr("r", d => d.r); // Restore the original size

                    // Remove the text label
                    svg.select("#hoverLabel").remove();
                    svg.select("#hoverLabelBg").remove();
                });




            node.append("text")
                .text(d => d.data.id)
                .attr("fill", "#000")
                .attr("font-size", "14px"); // Change the font size here

        });

        return () => {
            d3.select(currentRef).selectAll("svg").remove();
        }

    }, [filepath]);

    return (
        <div ref={ref}></div>
    );
};

export default BubbleChart;
