import React, { useEffect, useRef, useState } from 'react';
import * as d3 from 'd3';
import { useParams } from'react-router-dom';

function TreeVisualisation({}) {
    // const { filename } = useParams();
    const { filename: filenameFromParams } = useParams();
    const [filename, setFilename] = useState(filenameFromParams);
    const ref = useRef();

    useEffect(() => {
        setFilename(filenameFromParams)
        console.log(filenameFromParams);
         // <-- Add this line
        drawTree(filenameFromParams);
    }, [filenameFromParams]);

    const drawTree = (filename) => {
        // clear SVG in case of any previous renders
        d3.select(ref.current).selectAll("*").remove();

        var margin = {top: 20, right: 90, bottom: 30, left: 90},
            width = 2000 - margin.left - margin.right,
            height = 1000 - margin.top - margin.bottom;

        var svg = d3.select(ref.current)
            .attr("width", "100%")
            .attr("height", "100%")
            .attr("viewBox", `0 0 ${width + margin.right + margin.left} ${height + margin.top + margin.bottom}`)
            .attr("preserveAspectRatio", "xMidYMid meet")
            .append("g")
            .attr("transform", "translate(" + margin.left + "," + margin.top + ")");

        var treemap = d3.tree().size([height, width]).separation(function(a, b) {
            return (a.parent == b.parent ? 70 : 50);
        });

        d3.json(`http://localhost:3000/processed_data/${filename}`)
        .then(function(error, treeData) { 
            if (error) throw error;
            var root = d3.hierarchy(treeData, function(d) { return d.children; });
            root.x0 = height / 2;
            root.y0 = width / 2;
            // Collapse after the second level
            root.children.forEach(collapse);
            update(root);
        })
        .catch(error => {
            console.error("Error occured while fetching and processing data: ", error);
        });

        // Include your functions collapse, update, etc. here.
        function collapse(d) {
            if(d.children) {
                d._children = d.children;
                d._children.forEach(collapse);
                d.children = null;
            }
        }

        function centerNode(source) {
            let scale = 0.8;
            let x = -source.y0 * scale + width / 2;
            let y = -source.x0 * scale + height / 2;
            svg.transition()
                .duration(750)
                .call(d3.zoomIdentity.translate(x, y).scale(scale));
        }

        function update(source) {
            // Complete update function and other related functions go here
        }

        function update(source) {
            var i = 0,
            duration = 750;

            // Assigns the x and y position for the nodes
            var treeData = treemap(root);

            // Compute the new tree layout.
            var nodes = treeData.descendants(),
                links = treeData.descendants().slice(1);

            // Normalize for fixed-depth.
            nodes.forEach(function(d){ d.y = d.depth * 1200});
            }

            // ****************** Nodes section ***************************

            // Update the nodes...
            var node = svg.selectAll('g.node')
            .data(nodes, function(d) {return d.id || (d.id = ++i); });

            // Enter any new nodes at the parent's previous position.
            var nodeEnter = node.enter().append('g')
                .attr('class', 'node')
                .attr("transform", function(d) {
                    return "translate(" + source.y0 + "," + source.x0 + ")";
                })
            .on('click', click);

            // Add Circle for the nodes
            nodeEnter.append('circle')
                .attr('class', 'node')
                .attr('r', 1e-4)
                .style("fill", function(d) {
                    return d._children ? "lightsteelblue" : "#fff";
                });

            // Add labels for the nodes
            nodeEnter.append('text')
                .attr("dy", ".35em")
                .attr("x", function(d) {
                    return d.children || d._children ? -13 : 13;
                })
                .attr("text-anchor", function(d) {
                    return d.children || d._children ? "end" : "start";
                })
                .text(function(d) { return d.data.name; });

// Rest of the code goes here, similar to your HTML file.
}

    return (
        <div>
            <h1>{filename}</h1>
            {/* <h2>TESTING</h2> */}
            <svg ref={ref}></svg>
        </div>
    );
}

export default TreeVisualisation;
